import React from "react";
import ListItem from "./ListItem";
import "./index.css";

const ListContainer = ({ listNumber, list, onListSelection }) => {
  return (
    <div className="list-container">
      <div className="check-heading">
        <input
          id={`myCheckbox + ${listNumber}`}
          type="checkbox"
          onChange={() => onListSelection(listNumber)}
        />
        <label htmlFor={`myCheckbox + ${listNumber}`} className="list-head">
          List {listNumber}
        </label>
      </div>
      {list.map((item) => (
        <ListItem key={item.id} item={item} />
      ))}
    </div>
  );
};

export default ListContainer;
