import React, { useState, useEffect } from "react";
import axios from "axios";
import ListView from "./components/ListView";
import ListCreationView from "./components/ListCreationView";
import Loader from "./components/Loader";
import "./App.css";

const API_URL = "https://apis.ccbp.in/list-creation/lists"; // Replace with actual API URL

function App() {
  const [lists, setLists] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const [selectedLists, setSelectedLists] = useState([]);
  const [view, setView] = useState("all"); // "all" or "creation"

  const fetchLists = async () => {
    setLoading(true);
    setError(false);
    try {
      const response = await axios.get(API_URL);
      setLists(response.data);
    } catch {
      setError(true);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchLists();
  }, []);

  const handleRetry = () => fetchLists();

  const handleListSelection = (id) => {
    if (selectedLists.includes(id)) {
      setSelectedLists(selectedLists.filter((listId) => listId !== id));
    } else if (selectedLists.length < 2) {
      setSelectedLists([...selectedLists, id]);
    }
  };

  const handleCreateNewList = () => {
    if (selectedLists.length !== 2) {
      alert("You should select exactly 2 lists to create a new list");
    } else {
      setView("creation");
    }
  };

  const handleCancel = () => {
    setSelectedLists([]);
    setView("all");
  };

  const handleUpdate = (updatedLists) => {
    setLists(updatedLists);
    setView("all");
  };

  return (
    <div className="app-container">
      {loading && <Loader />}
      {error && (
        <div className="error-view">
          <p>Failed to load lists. Please try again.</p>
          <button onClick={handleRetry}>Try Again</button>
        </div>
      )}
      {!loading && !error && view === "all" && (
        <ListView
          lists={lists}
          selectedLists={selectedLists}
          onListSelection={handleListSelection}
          onCreateNewList={handleCreateNewList}
        />
      )}
      {view === "creation" && (
        <ListCreationView
          lists={lists}
          selectedLists={selectedLists}
          onCancel={handleCancel}
          onUpdate={handleUpdate}
        />
      )}
    </div>
  );
}

export default App;
