import React, { useState, useEffect } from "react";
import { RotatingLines } from "react-loader-spinner";
import axios from "axios";
import "./App.css";

const API_URL = "https://apis.ccbp.in/list-creation/lists"; // Replace with the actual API URL

function App() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const [data, setData] = useState([]);
  const [lists, setLists] = useState({});
  const [listOrder, setListOrder] = useState([]); // To track list order
  const [selectedLists, setSelectedLists] = useState([]);
  const [newListView, setNewListView] = useState(false);
  const [newListKey, setNewListKey] = useState(null); // Track new list number
  const [showCreateNewList, setShowCreateNewList] = useState(true); // Track visibility of Create New List button

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    setError(false);
    try {
      const response = await axios.get(API_URL);
      const { lists, total } = response.data; // Replace with actual response properties
      const groupedLists = lists.reduce((acc, item) => {
        if (!acc[item.list_number]) {
          acc[item.list_number] = [];
        }
        acc[item.list_number].push(item);
        return acc;
      }, {});

      // Set the lists and the order they should appear
      const sortedListNumbers = Object.keys(groupedLists).sort((a, b) => a - b);
      setData(lists);
      setLists(groupedLists);
      setListOrder(sortedListNumbers); // Save the order of list numbers
      setLoading(false);
    } catch (err) {
      setError(true);
      setLoading(false);
    }
  };

  const handleRetry = () => {
    fetchData();
  };

  const handleListSelection = (listNumber) => {
    const updatedSelection = selectedLists.includes(listNumber)
      ? selectedLists.filter((num) => num !== listNumber)
      : [...selectedLists, listNumber];
    setSelectedLists(updatedSelection);
  };

  const handleCreateNewList = () => {
    if (selectedLists.length !== 2) {
      let alertForCheckEl = document.getElementById("alertForCheck");
      alertForCheckEl.textContent =
        "*You should select exactly 2 lists to create a new list";
      return;
    }

    // Hide the Create New List section after it is clicked
    setShowCreateNewList(false);

    // Sort the selected list numbers to determine the correct order
    const [firstList, secondList] = selectedLists.sort((a, b) => a - b);

    // Determine the next available list number by checking the highest existing list number
    const nextListNumber = Math.max(...Object.keys(lists).map(Number)) + 1;

    // Set the new list key to be the next list number
    const newListKey = nextListNumber.toString();
    setNewListKey(newListKey);

    // Insert the new list dynamically between the selected lists
    setLists((prev) => {
      const updatedLists = { ...prev };
      const newOrder = [];

      // Create a new order where the new list is inserted between the selected lists
      Object.keys(updatedLists)
        .sort((a, b) => a - b)
        .forEach((key) => {
          newOrder.push(key);
          if (key === firstList.toString()) {
            newOrder.push(newListKey); // Insert new list after the first selected list
          }
        });

      const reorderedLists = {};
      newOrder.forEach((key) => {
        reorderedLists[key] = updatedLists[key] || [];
      });
      reorderedLists[newListKey] = []; // Add an empty array for the new list (0 items)

      return reorderedLists;
    });

    setListOrder((prevOrder) => {
      const newOrder = [...prevOrder];
      const insertIndex = newOrder.indexOf(firstList.toString()) + 1; // Position after the first selected list
      newOrder.splice(insertIndex, 0, newListKey); // Insert the new list in the order
      return newOrder;
    });

    setNewListView(true); // Show the new list view
  };

  const handleMoveItem = (item, fromList) => {
    const isChecked = selectedLists.includes(fromList);

    if (isChecked) {
      setLists((prevLists) => {
        const updatedLists = { ...prevLists };

        // Remove the item from the original list (fromList) if it exists
        const updatedFromList = (updatedLists[fromList] || []).filter(
          (i) => i.id !== item.id
        );

        // Ensure List 3 exists in the updated lists
        if (!updatedLists["3"]) {
          updatedLists["3"] = [];
        }

        // Add the item to List 3, only if it doesn't already exist
        if (!updatedLists["3"].some((i) => i.id === item.id)) {
          updatedLists["3"].push(item);
        }

        // Remove any duplicate items in List 3 by ensuring uniqueness based on the `id`
        updatedLists["3"] = updatedLists["3"].filter(
          (value, index, self) =>
            index === self.findIndex((t) => t.id === value.id) // Remove duplicates
        );

        return {
          ...updatedLists,
          [fromList]: updatedFromList, // Update the original list by removing the item
        };
      });
    } else {
      alert(
        "Please check the checkbox to select the list before moving items."
      );
    }
  };

  const handleCancel = () => {
    setNewListView(false);
    setSelectedLists([]); // Uncheck all checkboxes
    fetchData();
    setShowCreateNewList(true); // Show the "Create New List" button again
  };

  const handleUpdate = () => {
    setNewListView(false);
    setSelectedLists([]); // Uncheck all checkboxes
    setShowCreateNewList(true); // Show the "Create New List" button again
  };

  return (
    <div className="App">
      {loading ? (
        <div className="loader">
          <RotatingLines
            strokeColor="grey"
            strokeWidth="5"
            animationDuration="0.75"
            width="96"
            visible={true}
          />
        </div>
      ) : error ? (
        <div className="error-view">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/e/e0/Network_error_icon.svg"
            alt="Network Error"
            style={{ width: "100px", height: "100px" }}
          />
          <p>Failed to load data. Please try again.</p>
          <button onClick={handleRetry}>Try Again</button>
        </div>
      ) : (
        <div className="main-container">
          <div className="head-btn-container">
            {showCreateNewList && (
              <>
                <h1 className="main-heading">List Creation</h1>
                <button
                  type="button"
                  className="add-new-list-button"
                  onClick={handleCreateNewList}
                >
                  Create a New List
                </button>
              </>
            )}
            <span id="alertForCheck"></span>
          </div>
          <div className={`lists-container ${newListView ? "row" : ""}`}>
            {newListView ? (
              <div>
                <div className="list-creation-view">
                  {listOrder.map((listNumber) => (
                    <div className="list" key={listNumber}>
                      {listNumber === newListKey ? (
                        <div className="new-list-heading">
                          <h3>
                            List {newListKey} ({lists[newListKey].length})
                          </h3>
                        </div>
                      ) : (
                        <>
                          <h3>
                            List {listNumber} ({lists[listNumber].length} items)
                          </h3>
                          <div className="list-item-container">
                            {lists[listNumber].map((item) => (
                              <div className="list-item" key={item.id}>
                                <span className="name">{item.name}</span>
                                <span className="description">
                                  {item.description}
                                </span>
                                <div className="arrow-buttons">
                                  {listNumber === "1" && (
                                    <button
                                      className="arrow"
                                      onClick={() => handleMoveItem(item, "1")}
                                    >
                                      →
                                    </button>
                                  )}
                                  {listNumber === "2" && (
                                    <button
                                      className="arrow"
                                      onClick={() => handleMoveItem(item, "2")}
                                    >
                                      →
                                    </button>
                                  )}
                                  {listNumber === "3" && (
                                    <>
                                      <button
                                        className="arrow"
                                        onClick={() =>
                                          handleMoveItem(item, "3")
                                        }
                                      >
                                        ←
                                      </button>
                                      <button
                                        className="arrow"
                                        onClick={() =>
                                          handleMoveItem(item, "3")
                                        }
                                      >
                                        →
                                      </button>
                                    </>
                                  )}
                                </div>
                              </div>
                            ))}
                          </div>
                        </>
                      )}
                    </div>
                  ))}
                </div>
                <div className="actions">
                  <button onClick={handleCancel}>Cancel</button>
                  <button onClick={handleUpdate}>Update</button>
                </div>
              </div>
            ) : (
              <>
                <div className="list-selection">
                  {listOrder.map((listNumber) => (
                    <div key={listNumber} className="list-container">
                      <div className="check-heading">
                        <input
                          id={`myCheckbox + ${listNumber}`}
                          type="checkbox"
                          checked={selectedLists.includes(listNumber)}
                          onChange={() => handleListSelection(listNumber)}
                        />
                        <label
                          htmlFor={`myCheckbox + ${listNumber}`}
                          className="list-head"
                        >
                          List {listNumber}
                        </label>
                      </div>
                      {lists[listNumber].map((item) => (
                        <div className="list-item" key={item.id}>
                          <span className="name">{item.name}</span>
                          <span className="description">
                            {item.description}
                          </span>
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
