import './index.css'

function Review(props) {
  const {eachReview, onUpdateFeedBack} = props
  const {name, imageUrl, id} = eachReview

  function onClickFeedBack() {
    onUpdateFeedBack(id)
  }

  return (
    <li className="review-container">
      <button type="button" className="image-button" onClick={onClickFeedBack}>
        <img src={imageUrl} alt={name} />
      </button>

      <p className="image-des">{name}</p>
    </li>
  )
}

export default Review
