import React from "react";
import "./ListView.css";

function ListView({ lists, selectedLists, onListSelection, onCreateNewList }) {
  return (
    <div className="list-view">
      <h1>All Lists</h1>
      <div className="lists-container">
        {lists.map((list) => (
          <div
            key={list.id}
            className={`list-container ${
              selectedLists.includes(list.id) ? "selected" : ""
            }`}
            onClick={() => onListSelection(list.id)}
          >
            <h3>{list.name}</h3>
            <ul>
              {list.items.map((item) => (
                <li key={item.id}>{item.name}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      <button onClick={onCreateNewList}>Create a new list</button>
    </div>
  );
}

export default ListView;
