import react from 'react'
import './index.css'

import Review from '../Review'

class Feedback extends react.Component {
  state = {feedbackSubmitted: false}

  onUpdateFeedBack = () => {
    this.setState(prevState => ({
      feedbackSubmitted: !prevState.feedbackSubmitted,
    }))
  }

  onUpdateInterface = () => {
    const {feedbackSubmitted} = this.state
    const {resources} = this.props
    const {emojis, loveEmojiUrl} = resources
    if (feedbackSubmitted === false) {
      return (
        <div className="feedback-container">
          <h1 className="heading">
            How satisfied are with our customer support performance?
          </h1>
          <ul className="review-list">
            {emojis.map(eachData => (
              <Review
                key={eachData.id}
                eachReview={eachData}
                onUpdateFeedBack={this.onUpdateFeedBack}
              />
            ))}
          </ul>
        </div>
      )
    }
    return (
      <div className="feedback-container">
        <img src={loveEmojiUrl} alt="love" className="love-img" />
        <h1 className="thankyou">Thank you</h1>
        <p className="description">
          We will use your feedback to improve our customer support performance
        </p>
      </div>
    )
  }

  render() {
    return <div className="main-container">{this.onUpdateInterface()}</div>
  }
}

export default Feedback
