import React, { useState } from "react";
import "./ListCreationView.css";

function ListCreationView({ lists, selectedLists, onCancel, onUpdate }) {
  const [newListItems, setNewListItems] = useState([]);

  const handleItemMove = (item, fromList, toList) => {
    const updatedLists = lists.map((list) => {
      if (list.id === fromList) {
        return { ...list, items: list.items.filter((i) => i.id !== item.id) };
      }
      if (list.id === toList) {
        return { ...list, items: [...list.items, item] };
      }
      return list;
    });
    setNewListItems(updatedLists);
  };

  return (
    <div className="list-creation-view">
      <h1>List Creation</h1>
      {/* Render selected lists */}
      <button onClick={onCancel}>Cancel</button>
      <button onClick={() => onUpdate(newListItems)}>Update</button>
    </div>
  );
}

export default ListCreationView;
